//import React from 'react';
//import useForm from './useForm';
//import { useForm } from 'react-hook-form';
import React, { useState } from "react";

function CreateBeerForm() {

    // const { register, handleSubmit } = useForm();

    // const handleChange = e => {
    //     const { inputValue } = e.target;
    //     const newValue = +inputValue;
    //     setValue(newLimit);
    //  };

    // const onSubmit = (data: any) => { console.log(data) };

    // const { values, handleChange, handleSubmit } = useForm(login);

    // function login() {
    //     console.log(values);
    // }


    const [beerName, setBeerName] = useState("");
    const [beerType, setBeerType] = useState("");
    const [hasCorn, setHasCorn] = useState("");
    const [ingredients, setIngridients] = useState("");

    const onSubmit = (data: any) => { console.log(data) };

    return (
        <form onSubmit={onSubmit}>
            <div className="textInput">
                <label>Beer Name</label>
                <input type="text" name="beerName" value={beerName} onChange={e => setBeerName(e.target.value)} required />
            </div>
            <div className="select">
                <label>Beer Type</label>
                <select name="beerType" value={beerType} onChange={e => setBeerType(e.target.value)}required >
                    <option aria-label="None" value="" />
                    <option value="Ale">Ale</option>
                    <option value="Lager">Lager</option>
                    <option value="Stout">Stout</option>
                </select>
            </div>
            <div className="checkbox">
                <label>Has Corn</label>
                <input type="checkbox" value={hasCorn} onChange={e => setHasCorn(e.target.value)} name="hasCorn" />
            </div>
            <div className="textArea">
                <label>Ingredients</label>
                <textarea name="ingredients" value={ingredients} onChange={e => setIngridients(e.target.value)} required />
            </div >
            <div className="submit">
                <button type="submit" value="addData">Submit</button>
            </div>
        </form>
    )
}

export default CreateBeerForm;
