import React from 'react';

interface Props {
    value?: any;
    onChange?: (arg0: any) => void;
}

const FormTextInput = React.forwardRef((props: Props, customRef) => {
    return (
        <div className="textInput">
            <label htmlFor="beerName">Beer Name: </label>
            <input
                data-test="beerName"
                type="text"
                name="beerName"
                value={props.value}
                onChange={props.onChange}
                ref={customRef}
                />
        </div>
    )
});

export default FormTextInput;
