import React from 'react';

function FormTextInput(props: { onChange: (arg0: any) => void; value: string | number | string[] | undefined; }) {

    function handleChange(event: { target: { value: any; }; }) {
        props.onChange(event.target.value);
    }

    return (
        <div className="textInput">
            <label>Beer Name</label>
            <input type="text" name="beerName" value={this.props.beerName} onChange={handleChange} required />
        </div>
    )
}

export default FormTextInput;
