import React from 'react';
import { useForm } from 'react-hook-form';

function CreateBeerForm(this: any) {

    const { register, handleSubmit } = useForm();

    state = {value: ''}

    const handleChange = (event: { target: { value: any; }; }) => {
        let value = event.target.value;
        this.setState({ value });
        this.props.performSearch(value);
    }

    const onSubmit = (value: any) => { console.log(value) };

    return (
        <form onSubmit={handleSubmit(onSubmit)}>
            <div className="textInput">
                <label>Beer Name</label>
                <input onChange={handleChange} type="text" name="beerName" ref={register} value={this.state.value} required />
            </div>
            <div className="select">
                <label>Beer Type</label>
                <select name="beerType" ref={register} required >
                    <option aria-label="None" value="" />
                    <option value="Ale">Ale</option>
                    <option value="Lager">Lager</option>
                    <option value="Stout">Stout</option>
                </select>
            </div>
            <div className="checkbox">
                <label>Has Corn</label>
                <input type="checkbox" ref={register} name="hasCorn" />
            </div>
            <div className="textArea">
                <label>Ingredients</label>
                <textarea name="ingredients" ref={register} required />
            </div >
            <div className="submit">
                <button type="submit" value="addData">Submit</button>
            </div>
        </form>
    )
}

export default CreateBeerForm;
