import React from 'react';
import FormikTextInput from '../Formik_Components/FormikTextInput';
import FormikSelect from '../Formik_Components/FormikSelect';
import FormikCheckbox from '../Formik_Components/FormikCheckbox';
import FormikTextArea from '../Formik_Components/FormikTextArea';
import Button from '@material-ui/core/Button';
import { Formik, Form } from 'formik';

function CreateBeerFormikForm() {
    return (
        <Formik
            initialValues={{ beerName: '', beerType: '', hasCorn: '', ingredients: '' }}
            onSubmit={data => {
                console.log(data);
            }}
        >
            {({ values, errors, touched, isSubmitting }) => (
                <Form className='form'>
                    <div>
                        {touched.beerName && errors.beerName && <p>{errors.beerName}</p>}
                        <FormikTextInput type='text' name='beerName' placeholder='beerName' />
                    </div>
                    <div>
                        {touched.beerType && errors.beerType && <p>{errors.beerType}</p>}
                        <FormikSelect name='beerType' placeholder='beerType' />
                    </div>
                    <div>
                        {touched.hasCorn && errors.hasCorn && <p>{errors.hasCorn}</p>}
                        <FormikCheckbox name='hasCorn' placeholder='hasCorn' />
                    </div>
                    <div>
                        {touched.ingredients && errors.ingredients && <p>{errors.ingredients}</p>}
                        <FormikTextArea type='text' name='ingredients' placeholder='ingredients' />
                    </div>
                    <Button
                        variant="contained"
                        data-test="formSubmit"
                        color="primary"
                        size="small"
                        //disabled={isSubmitting}
                    >
                        Submit
                    </Button>
                </Form>
            )}
        </Formik>

    );
}

export default CreateBeerFormikForm;
