import React from 'react';

function FormCheckbox(props: { onChange: (arg0: any) => void; value: string | number | string[] | undefined; }) {

    function handleChange(event: { target: { value: any; } }) {
        props.onChange(event.target.value);
    }

    return (
        <div className="checkbox">
            <label>
                Has Corn:
            <input id="x" value={props.value} onChange={handleChange} type="checkbox" name="hasCorn" />
            </label>
        </div>
    )
}

export default FormCheckbox;
