import React from 'react';

function FormTextArea(props: { onChange: (arg0: any) => void; value: string | number | string[] | undefined; }) {

    function handleChange(event: { target: { value: any; }; }) {
        props.onChange(event.target.value);
      }

    return (
        <div className="textArea">
            <label>
            Ingredients:
            <textarea name="ingredients" value={props.value} onChange={handleChange} required />
            </label>
        </div >
    )
}

export default FormTextArea;
