import React from 'react';
import FormTextInput from '../Form_Components/FormTextInput';
import FormSelect from '../Form_Components/FormSelect';
import FormCheckbox from '../Form_Components/FormCheckbox';
import FormTextArea from '../Form_Components/FormTextArea';

function CreateBeerForm() {

    // const { value, handleSubmit } = useForm();


    const [value, setValue] = React.useState("");
    const onSubmit = (data: any) => { console.log(data) };

    function handleChange(newValue: React.SetStateAction<string>) {
    setValue(newValue);
  }
    return (
        <form onSubmit={onSubmit}>
            <FormTextInput value={value} onChange={handleChange} />
            <FormSelect value={value} onChange={handleChange} />
            <FormCheckbox value={value} onChange={handleChange} />
            <FormTextArea value={value} onChange={handleChange} />
            <button type="submit" value="addData">Submit</button>
        </form>
    )
}

export default CreateBeerForm;
