import React from 'react';

interface Props<T> {
    value?: any;
    event?: any;
    onChange?: () => void;
}

function FormTextInput<T extends object>(props: Props<T>) {

    return (
        <div className="textInput">
            <label>Beer Name</label>
            <input type="text" name="beerName" value={props.value} required />
        </div>
    )
}

export default FormTextInput;
