import React, { RefObject } from 'react';

interface Props {
    ref: string | ((instance: HTMLInputElement | null) => void) | RefObject<HTMLInputElement> | null | undefined;
    value?: any;
    onChange?: (arg0: any) => void;
}

function FormTextInput(props: Props) {

    return (
        <div className="textInput">
            <label htmlFor="beerName">Beer Name: </label>
            <input data-test="beerName" ref={props.ref} type="text" name="beerName" value={props.value} onChange={props.onChange} required />
        </div>
    )
}

export default FormTextInput;
