import React, { RefObject } from 'react';

interface Props {
    ref: string | ((instance: HTMLTextAreaElement | null) => void) | RefObject<HTMLTextAreaElement> | null | undefined;
    value?: any;
    onChange?: (arg0: any) => void;
}

function FormTextArea(props: Props) {

    return (
        <div className="textArea">
            <label htmlFor="ingredients">Ingredients: </label>
            <textarea data-test="ingredients" name="ingredients" ref={props.ref} value={props.value} onChange={props.onChange} />
        </div >
    )
}

export default FormTextArea;
