import React from 'react';

interface Props {
    values?: any;
    onChange?: (arg0: any) => void;
}

function FormTextInput(props: Props) {

    const handleChange = (event: any) => {
        event.persist();
        setValues((values: any) => ({ ...values, [event.target.name]: event.target.value }));
    }


    return (
        <div className="textInput">
            <label>
                Beer Name:
            <input id="beerName" type="text" name="beerName" value={props.value} onChange={handleChange} required />
            </label>
        </div>
    )
}

export default FormTextInput;
